mapper.select:
SELECT user_id AS userId, username AS userName, name, password, role
FROM USER_ACCOUNT
WHERE username = #{id}

SELECT user_id 
FROM USER_ACCOUNT
WHERE username = #{username}

SELECT user_id AS userId, username AS userName, name, password, role
FROM USER_ACCOUNT
WHERE user_id = #{userId}

mapper.update:
UPDATE USER_ACCOUNT
SET 
    name = #{name}
WHERE 
    user_id = #{userId}
